mapper.select:
select 

from order_detail
where zddh = #{zddh,jdbcType=VARCHAR} and fddh = #{fddh,jdbcType=VARCHAR}

select a.fpqqlsh, a.fpqqlsh as lsh , GROUP_CONCAT(b.zddh) as zddh, b.hyid,b.fpdq,GROUP_CONCAT(b.ddsj) as ddsj,GROUP_CONCAT(b.fksj) as fksj, b.sqsj,b.fptt,GROUP_CONCAT(c.xmmc) as xmmc,GROUP_CONCAT(b.bzfp) as bzfp, a.fplx,a.jshj,a.hjse,a.hjje,b.shr,b.shrdh,b.jsdz,b.sqrk from kpxx a LEFT JOIN order_detail b ON a.fpqqlsh = b.fpqqlsh LEFT JOIN fpmx c ON a.fpqqlsh = c.fpqqlsh WHERE a.fpzt = '1' and a.fplx = '007' and a.xsfnsrsbh = #{nsrsbh}
and b.ddsj >= #{beginDate}
and b.ddsj <= #{endDate}
and b.zddh like CONCAT('%',#{zddh },'%' )
and b.hyid = like CONCAT('%',#{hyid},'%' )
and b.shrdh = like CONCAT('%',#{shrdh},'%' )
GROUP BY a.fpqqlsh,b.hyid,b.fpdq,b.sqsj,b.fptt,a.fplx,a.jshj,a.hjse,a.hjje,b.shr,b.shrdh,b.jsdz

select a.fpqqlsh, a.fpqqlsh as lsh , GROUP_CONCAT(b.zddh) as zddh, b.hyid,b.fpdq,GROUP_CONCAT(b.ddsj) as ddsj,GROUP_CONCAT(b.fksj) as fksj, b.sqsj,b.fptt,GROUP_CONCAT(c.xmmc) as xmmc,GROUP_CONCAT(b.bzfp) as bzfp, a.fplx,a.jshj,a.hjse,a.hjje,b.shr,b.shrdh,b.jsdz,b.sqrk from kpxx a LEFT JOIN order_detail b ON a.fpqqlsh = b.fpqqlsh LEFT JOIN fpmx c ON a.fpqqlsh = c.fpqqlsh WHERE a.fpzt = '1' and a.fplx = '004' and a.xsfnsrsbh = #{nsrsbh}
and b.ddsj >= #{beginDate}
and b.ddsj <= #{endDate}
and b.zddh like CONCAT('%',#{zddh },'%' )
and b.hyid = like CONCAT('%',#{hyid},'%' )
and b.shrdh = like CONCAT('%',#{shrdh},'%' )
GROUP BY a.fpqqlsh,b.hyid,b.fpdq,b.sqsj,b.fptt,a.fplx,a.jshj,a.hjse,a.hjje,b.shr,b.shrdh,b.jsdz

	select 
	k.fpqqlsh,o.zddh as ddh,o.hyid,o.fpdq, o.ddsj,o.fksj, o.sqsj, o.fptt,f.xmmc,o.bzfp, k.fplx,o.sqrk,
	o.shr,o.shrdh,o.jsdz,k.hjje,k.hjse,k.jshj,k.kplx,k.kprq,k.fpdm,k.fphm
	from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh  LEFT JOIN fpmx f ON k.fpqqlsh=f.fpqqlsh  where k.fpzt = '3' and k.fplx = '007' 
	
      and k.xsfnsrsbh = #{nsrsbh}
    
    
     and k.kprq >= #{beginDate} 
    
    
     and k.kprq <= #{endDate} 
    
    
     and o.hyid like CONCAT('%',#{hyid4q },'%' )
    
    
     and k.fphm like CONCAT('%',#{fphm4q },'%' )
    
    
     and o.zddh like CONCAT('%',#{ddh4q },'%' )
    
    
     and o.shrdh like CONCAT('%',#{sjh4q },'%' )
    
    ORDER BY k.fphm
    
    LIMIT #{startRow},#{rows}

	select 
	k.fpqqlsh,o.zddh as ddh,o.hyid,o.fpdq, o.ddsj,o.fksj, o.sqsj, o.fptt,f.xmmc,o.bzfp, k.fplx,o.sqrk,
	k.gmfnsrsbh,k.gmfyhzh,CONCAT(k.gmfdz,gmfdh) as gfdzdh,o.shr,o.shrdh,o.jsdz,k.hjje,k.hjse,k.jshj,k.kplx,k.kprq,k.fpdm,k.fphm
	from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh  LEFT JOIN fpmx f ON k.fpqqlsh=f.fpqqlsh  where k.fpzt = '3' and k.fplx = '004'  
	
      and k.xsfnsrsbh = #{nsrsbh}
    
    
     and k.kprq >= #{beginDate} 
    
    
     and k.kprq <= #{endDate} 
    
    
     and o.hyid like CONCAT('%',#{hyid4q },'%' )
    
    
     and k.fphm like CONCAT('%',#{fphm4q },'%' )
    
    
     and o.zddh like CONCAT('%',#{ddh4q },'%' )
    
    
     and o.shrdh like CONCAT('%',#{sjh4q },'%' )
    
    ORDER BY k.fphm
    
    LIMIT #{startRow},#{rows}

	select count(*)
	from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh  LEFT JOIN fpmx f ON k.fpqqlsh=f.fpqqlsh  where k.fpzt = '3' and k.fplx = '007'  
	
      and k.xsfnsrsbh = #{nsrsbh}
    
    
     and k.kprq >= #{beginDate} 
    
    
     and k.kprq <= #{endDate} 
    
    
     and o.hyid like CONCAT('%',#{hyid4q },'%' )
    
    
     and k.fphm like CONCAT('%',#{fphm4q },'%' )
    
    
     and o.zddh like CONCAT('%',#{ddh4q },'%' )
    
    
     and o.shrdh like CONCAT('%',#{sjh4q },'%' )

	select count(*)
	from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh  LEFT JOIN fpmx f ON k.fpqqlsh=f.fpqqlsh  where k.fpzt = '3' and k.fplx = '004'  
	
