     and k.fphm like CONCAT('%',#{fphm4q },'%' )
    
    
     and o.zddh like CONCAT('%',#{ddh4q },'%' )
    
    
     and o.shrdh like CONCAT('%',#{sjh4q },'%' )
    
    ORDER BY k.fphm
    
    LIMIT #{startRow},#{rows}

	select k.fpqqlsh,o.zddh,k.fddh,o.hym,o.hyid,o.shr,o.shrdh,o.sqsj,
