mapper.select:
	select 
	o.fpqqlsh,o.fpdq as fpdq,o.fpkh,k.kprq,k.fplx as fpzl,k.fpdm,k.fphm,k.gmfmc as gfmc,o.spzl as fpnr,k.bz,k.hjje,k.hjse,k.jshj,k.fpzt as fpjd,o.fptt as ttlx
  from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh where o.fpqqlsh=#{fpqqlsh}

    select 
    from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh LEFT JOIN fpmx f ON k.fpqqlsh=f.fpqqlsh where k.fpzt = '3' and k.hjje>0
	
      and k.fplx = #{fplx}
    
	
      and k.xsfnsrsbh = #{nsrsbh}
    
    
	   and k.kprq >= #{dateS} 
    
    
	   and k.kprq <= #{dateE} 
    
    
	   and o.zddh = #{ddh4q,jdbcType=VARCHAR} 
    
    
	   and o.shrdh = #{shrdh4q,jdbcType=VARCHAR} 
    
    
	   and o.hyid = #{hyid4q,jdbcType=VARCHAR} 
    
    
	   and k.fphm = #{fphm4q,jdbcType=VARCHAR} 
    
    ORDER BY k.kprq
    
    LIMIT #{startRow},#{rows}

  	select 
  	o.fpqqlsh,o.fpdq as fpdq,o.fpkh,k.kprq,k.fplx as fpzl,k.fpdm,k.fphm,k.gmfmc as gfmc,o.spzl as fpnr,k.bz,k.hjje,k.hjse,k.jshj,k.fpzt as fpjd,o.fptt as ttlx
    from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh where k.fpzt in('3','-2')  
	
      and k.xsfnsrsbh = #{nsrsbh}
    
    
	   and k.kprq >= #{dateS} 
    
    
	   and k.kprq <= #{dateE} 
    
    
	   and o.fpdq = #{kpdq4q,jdbcType=VARCHAR} 
    
    
	   and k.fplx = #{fpzl4q,jdbcType=VARCHAR} 
    
    
	   and o.spzl = #{fplx4q,jdbcType=VARCHAR} 
    
    
    	and o.fptt = #{fptt4q,jdbcType=VARCHAR} 
    
    ORDER BY k.kprq DESC
    
    LIMIT #{startRow},#{rows}

	select count(*)
    from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh LEFT JOIN fpmx f ON k.fpqqlsh=f.fpqqlsh where k.fpzt = '3' and k.hjje>0
	
      and k.fplx = #{fplx}
    
	
      and k.xsfnsrsbh = #{nsrsbh}
    
    
	   and k.kprq >= #{dateS} 
    
    
	   and k.kprq <= #{dateE} 
    
    
	   and o.zddh = #{ddh4q,jdbcType=VARCHAR} 
    
    
	   and o.shrdh = #{shrdh4q,jdbcType=VARCHAR} 
    
    
	   and o.hyid = #{hyid4q,jdbcType=VARCHAR} 
    
    
	   and k.fphm = #{fphm4q,jdbcType=VARCHAR}

  	select count(*) from kpxx k LEFT JOIN order_detail o ON o.fpqqlsh=k.fpqqlsh where k.fpzt in('3','-2')  
	
      and k.xsfnsrsbh = #{nsrsbh}
    
    
	   and k.kprq >= #{dateS} 
    
    
	   and k.kprq <= #{dateE} 
    
    
	   and o.fpdq = #{kpdq4q,jdbcType=VARCHAR} 
    
    
	   and k.fplx = #{fpzl4q,jdbcType=VARCHAR} 
    
    
	   and o.spzl = #{fplx4q,jdbcType=VARCHAR} 
    
    
    	and o.fptt = #{fptt4q,jdbcType=VARCHAR}
